import React, { useState, useEffect } from 'react';
import Modal from './Modal'; // Assuming you might need modals in the future
import '../styles/StaffDashboard.css';

const StaffDashboard = () => {
  const [grievances, setGrievances] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const grievancesPerPage = 5;

  useEffect(() => {
    const fetchGrievances = () => {
      // Dummy grievances data, replace with real API call
      const dummyGrievances = [
        { id: 1, category: 'Academic', description: 'Issue with syllabus', status: 'Pending' },
        { id: 2, category: 'Administration', description: 'Problem with admin staff', status: 'Resolved' },
        { id: 3, category: 'Facilities', description: 'Broken chair in classroom', status: 'In Progress' },
        { id: 4, category: 'Academic', description: 'Late grade submission', status: 'Pending' },
        { id: 5, category: 'Administration', description: 'Lost student ID', status: 'Resolved' },
        { id: 6, category: 'Facilities', description: 'Air conditioning not working', status: 'Pending' },
        // Add more as needed
      ];
      setGrievances(dummyGrievances);
    };

    fetchGrievances();
  }, []);

  const handlePageChange = (pageNumber) => setCurrentPage(pageNumber);

  // Calculate the current grievances to display
  const indexOfLastGrievance = currentPage * grievancesPerPage;
  const indexOfFirstGrievance = indexOfLastGrievance - grievancesPerPage;
  const currentGrievances = grievances.slice(indexOfFirstGrievance, indexOfLastGrievance);

 return (
    <div className="dashboard-container">
      <div className="main-section">
        <div className="good-job-tile">
          <h3>Good Job, Sarah. Keep Going!!</h3>
          <p>90% of grievances assigned to you are closed. Keep it up and improve your result. Progress is very good!!!</p>
          {/* Submit Grievance Button Banner */}
                    
          {/* Grievance Modal */}
          <Modal show={showGrievanceModal} handleClose={handleCloseGrievanceModal} title="Submit Grievance">
            <form onSubmit={handleGrievanceSubmit}>
              <div className="form-group">
                <label htmlFor="category">Category:</label>
                <select id="category" className="form-control">
                  <option value="Academic">Academic</option>
                  <option value="Administration">Administration</option>
                  <option value="Facilities">Facilities</option>
                  {/* Add more options as needed */}
                </select>
              </div>
              <div className="form-group">
                <label htmlFor="description">Description:</label>
                <textarea id="description" className="form-control" rows="4" placeholder="Describe your grievance"></textarea>
              </div>
              <div className="form-group">
                <label htmlFor="fileUpload">Attach File:</label>
                <input type="file" id="fileUpload" className="form-control-file" />
              </div>
              <button type="submit" className="btn btn-primary">Submit</button>
            </form>
          </Modal>
          
          <table className="grievance-table">
            <thead>
              <tr>
                <th>Grievance ID</th>
                <th>Category</th>
                <th>Description</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {grievances.map((grievance) => (
                <tr key={grievance.id}>
                  <td>{grievance.id}</td>
                  <td>{grievance.category}</td>
                  <td>{grievance.description}</td>
                  <td>{grievance.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
      <div className="sidebar-section">
        {/* Announcements and Notifications Section */}
        <div className="announcements-tile">
          <h3>Announcements & Notifications</h3>
          <ul className="announcements-list">
            <li>New grading policy effective from next semester.</li>
            <li>Library will be closed for maintenance on 5th July.</li>
            <li>Submit your assignments by the end of this month.</li>
          </ul>
        </div>
        {/* Updated Upcoming Class Section */}
        <div className="upcoming-class-tile">
          <h3>Upcoming Class</h3>
          <ul className="upcoming-class-list">
            <li>
              <span className="class-name">Cara Stevens</span>
              <span className="class-time">Mathematics - 12 June '20, 09:00-10:00</span>
            </li>
            <li>
              <span className="class-name">Airi Satou</span>
              <span className="class-time">Computer Studies - 13 June '20, 11:00-12:00</span>
            </li>
            <li>
              <span className="class-name">Jens Brincker</span>
              <span className="class-time">Geography - 15 June '20, 09:30-10:30</span>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default StaffDashboard;
