import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Sidebar from './components/Sidebar';
import StudentDashboard from './components/StudentDashboard';
import StaffDashboard from './components/StaffDashboard';
import AdminDashboard from './components/AdminDashboard';
import SignIn from './components/SignIn';
import SignUp from './components/SignUp';

function App() {
  const [userRole, setUserRole] = useState(null); // State to store the user role

  const handleRoleChange = (role) => {
    setUserRole(role);
  };

  return (
    <Router>
      <div className="app-container">
        {userRole && <Sidebar />} {/* Render Sidebar only if userRole is set */}
        <div className="main-content">
          {userRole && <Header />} {/* Render Header only if userRole is set */}
          <Routes>
            <Route path="/" element={<SignIn onRoleChange={handleRoleChange} />} />
            <Route path="/signin" element={<SignIn onRoleChange={handleRoleChange} />} />
            <Route path="/signup" element={<SignUp />} />

            {/* Conditional Routes based on role */}
            <Route path="/student/dashboard" element={userRole === 'Student' ? <StudentDashboard /> : <Navigate to="/signin" />} />
            <Route path="/staff/dashboard" element={userRole === 'Staff' ? <StaffDashboard /> : <Navigate to="/signin" />} />
            <Route path="/admin/dashboard" element={userRole === 'Admin' ? <AdminDashboard /> : <Navigate to="/signin" />} />

            {/* Add more routes as needed */}
          </Routes>
          {userRole && <Footer />} {/* Render Footer only if userRole is set */}
        </div>
      </div>
    </Router>
  );
}

export default App;
