import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Sidebar from './components/Sidebar';
import StudentDashboard from './components/StudentDashboard';
import StaffDashboard from './components/StaffDashboard';
import AdminDashboard from './components/AdminDashboard';
import SignIn from './components/SignIn';
import SignUp from './components/SignUp';

function App() {
  const [userRole, setUserRole] = useState(null); // State to store the user role

  const handleRoleChange = (role) => {
    setUserRole(role);
  };

  return (

        <div className={`app-container ${isSidebarOpen ? 'sidebar-open' : 'sidebar-collapsed'}`}>
      {!noAuthRoutes.includes(location.pathname) ? (
        <>
          <Sidebar 
            isSidebarOpen={isSidebarOpen} 
            userRole={userRole} 
            userName={userName}
            userProfilePic={userProfilePic}
          />
          <div className="main-area">
            <Header toggleSidebar={toggleSidebar} />
            <div className="main-content">
              <Routes>
                {userRole === 'Student' && (
                  <Route path="/student/dashboard" element={<StudentDashboard />} />
                )}
                {userRole === 'Staff' && (
                  <Route path="/staff/dashboard" element={<StaffDashboard />} />
                )}
                {userRole === 'Admin' && (
                  <Route path="/admin/dashboard" element={<AdminDashboard />} />
                )}
                {/* Add more routes as needed */}
              </Routes>
              <Footer />
            </div>
          </div>
        </>
      ) : (
        <div className="auth-content">
          <Routes>
            <Route path="/signin" element={<SignIn onRoleChange={handleRoleChange} />} />
            <Route path="/signup" element={<SignUp />} />

          </Routes>
        </div>
      )}
    </div>

  );
}

export default App;
